import React from 'react';
import styled from 'styled-components';

const AboutContainer = styled.div`
  min-height: 100vh;
  padding: 4rem 2rem;
  background: linear-gradient(135deg, #0a0a2a 0%, #1a1a3a 100%);
`;

const Section = styled.section`
  max-width: 1200px;
  margin: 0 auto;
  padding: 2rem 0;
`;

const Title = styled.h2`
  font-size: 3rem;
  margin-bottom: 2rem;
  background: linear-gradient(90deg, #00f2fe 0%, #4facfe 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  opacity: 0;
  transform: translateY(20px);
  animation: fadeIn 1s ease forwards;

  @keyframes fadeIn {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const TimelineContainer = styled.div`
  position: relative;
  padding: 2rem 0;
`;

const TimelineLine = styled.div`
  position: absolute;
  left: 50%;
  width: 4px;
  height: 100%;
  background: linear-gradient(to bottom, #00f2fe, #4facfe);
  transform: translateX(-50%);
`;

const TimelineItem = styled.div`
  position: relative;
  width: 50%;
  padding: 2rem;
  margin-bottom: 2rem;
  opacity: 0;
  transform: translateX(-50px);
  animation: fadeInRight 0.5s ease forwards;
  
  &:nth-child(odd) {
    margin-left: auto;
    text-align: right;
    transform: translateX(50px);
    animation: fadeInLeft 0.5s ease forwards;
  }
  
  &:nth-child(even) {
    margin-right: auto;
  }

  @keyframes fadeInRight {
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  @keyframes fadeInLeft {
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }
`;

const TimelineDot = styled.div`
  position: absolute;
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background: linear-gradient(135deg, #00f2fe 0%, #4facfe 100%);
  top: 50%;
  transform: translateY(-50%);
  
  ${TimelineItem}:nth-child(odd) & {
    left: -10px;
  }
  
  ${TimelineItem}:nth-child(even) & {
    right: -10px;
  }
`;

const TimelineContent = styled.div`
  background: rgba(255, 255, 255, 0.05);
  padding: 2rem;
  border-radius: 10px;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
`;

const TimelineYear = styled.h3`
  color: #00f2fe;
  margin-bottom: 1rem;
  font-size: 1.5rem;
`;

const TimelineDescription = styled.p`
  color: rgba(255, 255, 255, 0.8);
  line-height: 1.6;
`;

const About: React.FC = () => {
  const timelineData = [
    {
      year: '2024',
      description: 'Founded with a vision to build future-proof software solutions',
    },
    {
      year: '2026',
      description: 'Expanded team and established our first innovation lab',
    },
    {
      year: '2028',
      description: 'Launched our first AI-powered automation platform',
    },
    {
      year: '2030',
      description: 'Opened international offices and expanded global reach',
    },
    {
      year: '2035',
      description: 'Pioneered quantum computing integration in our solutions',
    },
    {
      year: '2040',
      description: 'Established as a leader in sustainable tech development',
    },
    {
      year: '2045',
      description: 'Launched our first space-based computing solutions',
    },
    {
      year: '2049',
      description: 'Continuing to push boundaries in software innovation',
    },
  ];

  return (
    <AboutContainer>
      <Section>
        <Title>Our Journey to the Future</Title>
        <TimelineContainer>
          <TimelineLine />
          {timelineData.map((item, index) => (
            <TimelineItem key={index} style={{ animationDelay: `${index * 0.2}s` }}>
              <TimelineDot />
              <TimelineContent>
                <TimelineYear>{item.year}</TimelineYear>
                <TimelineDescription>{item.description}</TimelineDescription>
              </TimelineContent>
            </TimelineItem>
          ))}
        </TimelineContainer>
      </Section>
    </AboutContainer>
  );
};

export default About;
