import React from 'react';
import styled from 'styled-components';

const AboutContainer = styled.section`
  padding: 4rem 2rem;
  background: #ffffff;
  min-height: 100vh;
  display: flex;
  align-items: center;
`;

const AboutContent = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: 4rem;
  align-items: center;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 2rem;
  }
`;

const AboutText = styled.div`
  opacity: 0;
  transform: translateX(-20px);
  animation: fadeInLeft 1s ease forwards;

  @keyframes fadeInLeft {
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }
`;

const Title = styled.h2`
  font-size: 2.5rem;
  margin-bottom: 1.5rem;
  color: #0a0a2a;
  opacity: 0;
  animation: fadeIn 1s ease forwards 0.5s;

  @keyframes fadeIn {
    to {
      opacity: 1;
    }
  }
`;

const Description = styled.p`
  font-size: 1.1rem;
  line-height: 1.6;
  color: #666;
  margin-bottom: 2rem;
  opacity: 0;
  animation: fadeIn 1s ease forwards 1s;
`;

const ImageContainer = styled.div`
  position: relative;
  opacity: 0;
  transform: translateX(20px);
  animation: fadeInRight 1s ease forwards;

  @keyframes fadeInRight {
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }

  &::before {
    content: '';
    position: absolute;
    top: -20px;
    left: -20px;
    width: 100%;
    height: 100%;
    border: 2px solid #00f2fe;
    z-index: -1;
  }
`;

const Image = styled.img`
  width: 100%;
  height: auto;
  display: block;
  border-radius: 4px;
`;

const About: React.FC = () => {
  return (
    <AboutContainer>
      <AboutContent>
        <AboutText>
          <Title>About Us</Title>
          <Description>
            We are a team of passionate developers and designers dedicated to building innovative digital solutions. Our mission is to create products that not only meet today's needs but are also built to thrive in the future.
          </Description>
          <Description>
            With years of experience in web development, mobile applications, and cloud solutions, we help businesses transform their ideas into reality. We believe in clean code, user-centered design, and continuous innovation.
          </Description>
        </AboutText>
        <ImageContainer>
          <Image src="/images/about.jpg" alt="Our team working together" />
        </ImageContainer>
      </AboutContent>
    </AboutContainer>
  );
};

export default About;
