import React, { useEffect, useRef } from 'react';
import styled from 'styled-components';

const ServicesContainer = styled.section`
  min-height: 100vh;
  background: #000;
  position: relative;
  overflow: hidden;
  padding: 8rem 2rem;
  color: #fff;
  display: flex;
  align-items: center;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, rgba(0,0,0,0.9), rgba(0,0,0,0.7));
    z-index: 1;
  }
`;

const Section = styled.div`
  position: relative;
  z-index: 2;
  max-width: 1400px;
  margin: 0 auto;
`;

const Title = styled.h2`
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 4rem;
  text-align: center;
  background: linear-gradient(90deg, #fff, #00f2fe);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  letter-spacing: -0.02em;
  line-height: 1.1;
  opacity: 0;
  transform: translateY(30px);
  animation: fadeInUp 1s ease forwards;

  @keyframes fadeInUp {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }

  @media (max-width: 768px) {
    font-size: 2.5rem;
    margin-bottom: 2rem;
  }
`;

const ServicesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 2rem;

  @media (max-width: 1024px) {
    grid-template-columns: repeat(2, 1fr);
  }

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
  }
`;

const ServiceCard = styled.div`
  background: rgba(255, 255, 255, 0.05);
  border-radius: 20px;
  padding: 3rem;
  backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.1);
  transition: all 0.3s ease;
  opacity: 0;
  transform: translateY(50px);
  animation: fadeInUp 0.5s ease forwards;

  &:hover {
    transform: translateY(-10px);
    border-color: #00f2fe;
    box-shadow: 0 20px 40px rgba(0, 242, 254, 0.2);
  }

  @keyframes fadeInUp {
    to {
      opacity: 1;
      transform: translateY(0);
    }
  }
`;

const Icon = styled.div`
  width: 60px;
  height: 60px;
  background: linear-gradient(135deg, #00f2fe, #4facfe);
  border-radius: 12px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 2rem;
  transform: rotate(-5deg);
  transition: transform 0.3s ease;

  ${ServiceCard}:hover & {
    transform: rotate(0deg) scale(1.1);
  }
`;

const ServiceTitle = styled.h3`
  font-size: 1.8rem;
  font-weight: 600;
  margin-bottom: 1rem;
  color: #fff;
`;

const ServiceDescription = styled.p`
  font-size: 1.1rem;
  line-height: 1.6;
  color: rgba(255, 255, 255, 0.8);
  margin-bottom: 2rem;
`;

const ServiceFeatures = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const Feature = styled.li`
  display: flex;
  align-items: center;
  margin-bottom: 1rem;
  color: rgba(255, 255, 255, 0.8);
  font-size: 1rem;

  &::before {
    content: 'â†’';
    color: #00f2fe;
    margin-right: 0.5rem;
    font-weight: bold;
  }
`;

const servicesData = [
  {
    title: 'Web Development',
    description: 'Building modern, responsive websites with cutting-edge technologies and best practices.',
    features: [
      'React & Next.js Development',
      'Progressive Web Apps',
      'Performance Optimization',
      'SEO Implementation'
    ]
  },
  {
    title: 'Mobile Apps',
    description: 'Creating native and cross-platform mobile applications for iOS and Android.',
    features: [
      'React Native Development',
      'Native iOS & Android',
      'App Store Optimization',
      'Push Notifications'
    ]
  },
  {
    title: 'Cloud Solutions',
    description: 'Designing and implementing scalable cloud infrastructure and services.',
    features: [
      'AWS & Azure Integration',
      'Serverless Architecture',
      'Microservices',
      'DevOps Automation'
    ]
  },
  {
    title: 'UI/UX Design',
    description: 'Crafting beautiful, intuitive user interfaces and experiences.',
    features: [
      'User Research',
      'Wireframing & Prototyping',
      'Design Systems',
      'Accessibility'
    ]
  },
  {
    title: 'AI & Machine Learning',
    description: 'Developing intelligent solutions powered by artificial intelligence.',
    features: [
      'Machine Learning Models',
      'Natural Language Processing',
      'Computer Vision',
      'Predictive Analytics'
    ]
  },
  {
    title: 'Blockchain',
    description: 'Building secure, decentralized applications and smart contracts.',
    features: [
      'Smart Contract Development',
      'DApp Creation',
      'Tokenization',
      'Security Audits'
    ]
  }
];

const Services: React.FC = () => {
  const containerRef = useRef<HTMLDivElement>(null);
  const cardsRef = useRef<(HTMLDivElement | null)[]>([]);

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    const handleScroll = () => {
      const { top } = container.getBoundingClientRect();
      const windowHeight = window.innerHeight;
      
      if (top < windowHeight * 0.8) {
        cardsRef.current.forEach((card, index) => {
          if (card) {
            setTimeout(() => {
              card.style.opacity = '1';
              card.style.transform = 'translateY(0)';
            }, index * 100);
          }
        });
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <ServicesContainer id="services" ref={containerRef}>
      <Section>
        <Title>Our Services</Title>
        <ServicesGrid>
          {servicesData.map((service, index) => (
            <ServiceCard 
              key={index} 
              ref={el => {
                if (el) {
                  cardsRef.current[index] = el;
                }
              }}
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <Icon>
                <img src={`/images/service-${index + 1}.svg`} alt={service.title} />
              </Icon>
              <ServiceTitle>{service.title}</ServiceTitle>
              <ServiceDescription>{service.description}</ServiceDescription>
              <ServiceFeatures>
                {service.features.map((feature, idx) => (
                  <Feature key={idx}>{feature}</Feature>
                ))}
              </ServiceFeatures>
            </ServiceCard>
          ))}
        </ServicesGrid>
      </Section>
    </ServicesContainer>
  );
};

export default Services;
