import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';

const Nav = styled.nav`
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  z-index: 1000;
  background: var(--color-background);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
  border-bottom: 1px solid var(--color-border);
  height: 44px;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const NavContainer = styled.div`
  max-width: 980px;
  width: 100%;
  height: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 0 22px;
`;

const Logo = styled(Link)`
  font-size: 21px;
  font-weight: 600;
  color: var(--color-text-primary);
  opacity: 0;
  transform: translateX(-20px);
  animation: fadeInLeft 0.5s ease forwards;

  @keyframes fadeInLeft {
    to {
      opacity: 1;
      transform: translateX(0);
    }
  }
`;

const NavLinks = styled.div`
  display: flex;
  gap: 34px;
  height: 100%;
  align-items: center;

  @media (max-width: 768px) {
    display: none;
  }
`;

const NavLink = styled(Link)`
  font-size: 12px;
  font-weight: 400;
  color: var(--color-text-secondary);
  transition: color 0.3s ease;
  position: relative;
  height: 100;
  display: flex;
  align-items: center;

  &:hover {
    color: var(--color-text-primary);
  }

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    height: 1px;
    background-color: var(--color-text-primary);
    transform: scaleX(0);
    transition: transform 0.3s ease;
  }

  &:hover::after {
    transform: scaleX(1);
  }
`;

const MobileMenuButton = styled.button`
  display: none;
  background: none;
  border: none;
  color: var(--color-text-primary);
  font-size: 21px;
  cursor: pointer;
  padding: 0 10px;
  height: 100%;
  align-items: center;

  @media (max-width: 768px) {
    display: flex;
  }
`;

const MobileMenu = styled.div<{ isOpen: boolean }>`
  display: none;
  position: fixed;
  top: 44px;
  left: 0;
  right: 0;
  background: var(--color-background);
  border-bottom: 1px solid var(--color-border);
  padding: 0 22px;
  transform: translateY(${props => props.isOpen ? '0' : '-100%'});
  transition: transform 0.3s ease;

  @media (max-width: 768px) {
    display: block;
  }
`;

const MobileNavLinks = styled.div`
  display: flex;
  flex-direction: column;
  gap: 0;
`;

const MobileNavLink = styled(Link)`
  display: block;
  padding: 12px 0;
  font-size: 17px;
  color: var(--color-text-primary);
  border-bottom: 1px solid var(--color-border);

  &:last-child {
    border-bottom: none;
  }
`;

const Navigation: React.FC = () => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  return (
    <>
      <Nav>
        <NavContainer>
          <Logo to="/">Your Logo</Logo>
          <NavLinks>
            <NavLink to="/">Home</NavLink>
            <NavLink to="/about">About</NavLink>
            <NavLink to="/services">Services</NavLink>
            <NavLink to="/contact">Contact</NavLink>
          </NavLinks>
          <MobileMenuButton onClick={toggleMobileMenu}>
            ☰
          </MobileMenuButton>
        </NavContainer>
      </Nav>
      <MobileMenu isOpen={isMobileMenuOpen}>
        <MobileNavLinks>
          <MobileNavLink to="/" onClick={toggleMobileMenu}>Home</MobileNavLink>
          <MobileNavLink to="/about" onClick={toggleMobileMenu}>About</MobileNavLink>
          <MobileNavLink to="/services" onClick={toggleMobileMenu}>Services</MobileNavLink>
          <MobileNavLink to="/contact" onClick={toggleMobileMenu}>Contact</MobileNavLink>
        </MobileNavLinks>
      </MobileMenu>
    </>
  );
};

export default Navigation;
